package com.ywxs.blog.admin.config;import cn.hutool.http.HttpStatus;import com.ywxs.blog.common.util.Result;import io.jsonwebtoken.ExpiredJwtException;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.ResponseBody;/** * 统一异常处理类 */@ControllerAdvicepublic class BaseExceptionHandler {    @ExceptionHandler(value = Exception.class)    @ResponseBody    public Result error(Exception e) {        e.printStackTrace();        return new Result(false, HttpStatus.HTTP_INTERNAL_ERROR, e.getMessage());    }    @ExceptionHandler(value = ExpiredJwtException.class)    @ResponseBody    public Result loginError(ExpiredJwtException e) {        e.printStackTrace();        return new Result(false, HttpStatus.HTTP_UNAUTHORIZED, "登录已过期，请重新登录");    }}